

class Mascota():

    nombre = "Paco"
    estaVivo = False
    salud = 0.00
    hambre = 0.00
    felicidad = 0.00
    stamina = 0.00
    higiene = 0.00
    mood = 0.00

    #Constructor de la clase por defecto
    def  __init__ (self):
        self.estaVivo = True
        self.salud = 100.00
        self.hambre = 0.00
        self.felicidad = 100.00
        self.stamina = 100.00
        self.higiene = 100.00
        self.mood = 100.00
    
    #Constructor de la clase con nombre
    def  __init__ (self, nombre):
        self.nombre = nombre
        self.estaVivo = True
        self.salud = 100.00
        self.hambre = 0.00
        self.felicidad = 100.00
        self.stamina = 100.00
        self.higiene = 100.00
        self.mood = 100.00
    
    def alimentar():
        self.hambre -= 10.00
    
    def jugar():
        if(self.stamina <= 10.00):
            print()
            return
        if(self.stamina >= 10.00 and  self.felicidad <= 10.00):
            self.stamina -= 10.00
            self.felicidad += 10.00
    
    def limpiar():
        self.higiene += 10.00
